/**
 * Class representing a command.
 */
class Command {
  /**
   * Create a command.
   * @param {Object}   data                       The command data.
   * @param {String}   data.name                  The command name.
   * @param {String[]} [data.aliases=[]]          List of alias names for the command.
   * @param {String}   data.description           The command description.
   * @param {String[]} [data.parameters=[]]       List of paremeters that the command takes.
   * @param {String}   [data.permission='Anyone'] The name of the permission required to use the command.
   * @param {Function} data.run                   The command function.
   */
  constructor ({ name, aliases, description, parameters, permission, run }) {
    if (typeof name !== 'string') throw Error(`command cannot have name "${name}"`)
    if (typeof description !== 'string') throw Error(`command cannot have description "${description}"`)
    if (typeof run !== 'function') throw Error(`command cannot have run function "${run}"`)

    /**
     * The command name.
     * @type {String}
     */
    this.name = name
    /**
     * List of alias names for the command.
     * @type {String[]}
     */
    this.aliases = aliases || []
    /**
     * The command description.
     * @type {String}
     */
    this.description = description
    /**
     * List of paremeters that the command takes.
     * @type {String[]}
     */
    this.parameters = parameters || []
    /**
     * The name of the permission required to use the command.
     * @type {String}
     */
    this.permission = permission || 'Anyone'
    /**
     * The command function.
     * @type {Function}
     */
    this.run = run
  }
  /**
   * Get information about the command.
   * @return {String} Info string containing name and description.
   */
  help () {
    return `Name: ${this.name}\nDescription: ${this.description}\nParameters:${this.parameters.join(', ')}`
  }
}

module.exports = Command

/**
 * Class representing a database manager.
 */
class DatabaseManager {
  /**
   * Create a database manager.
   * @param {Object} DB_CREDENTIALS          The credentials to create the db connection. This should be filled in the config file.
   * @param {String} DB_CREDENTIALS.database The name of your database.
   * @param {String} DB_CREDENTIALS.host     The address of your database.
   * @param {String} DB_CREDENTIALS.user     The username to login with.
   * @param {String} DB_CREDENTIALS.password The password associated with your user.
   */
  constructor (DB_CREDENTIALS) {
    /**
     * The knex query builder.
     * @private
     * @type    {Function}
     */
    this._knex = require('knex')({ client: 'mysql', connection: DB_CREDENTIALS })
  }

  /**
   * Insert a guild into the guild_settings table.
   * @param  {String}             id The ID of the guild
   * @return {(Number|undefined)}    Returns 0 on success or undefined.
   */
  addClient (id) {
    return this.insert({ table: 'guild_settings', data: { id } })
  }

  /**
   * Insert a status into the statuses table.
   * @param  {Object}             status      The status to make default.
   * @param  {String}             status.name The name of the status.
   * @param  {Number}             status.type The type of the status.
   * @return {(Number|undefined)}             Returns 0 on success or undefined.
   */
  addStatus (status) {
    return this.insert({ table: 'statuses', data: status })
  }

  /**
   * Get data on a guild from the guild_settings table
   * @param  {String} id The ID of the guild.
   * @return {Object}    The guild data.
   */
  async getClient (id) {
    return (await this.select({ table: 'guild_settings', where: { id } }))[0]
  }

  /**
   * Get the statuses of the bot from the statuses table.
   * @return {Object[]} Array of statuses, name and type.
   */
  async getStatuses () {
    return this.select({ table: 'statuses' })
  }

  /**
   * Check saved guild data against live guild data.
   * @param {Collection<Guild>} guilds The bots collection of guilds at start up.
   */
  async initialize (guilds) {
    let tmpGuilds = new Map(guilds)
    const saved = await this.select({ table: 'guild_settings' })
    if (saved) {
      for (let i = 0; i < saved.length; i++) {
        const id = saved[i].id
        const guild = tmpGuilds.get(id)
        if (guild) {
          tmpGuilds.delete(id)
          if (saved[i].vip && !guild.roles.get(saved[i].vip)) this.updateClient(id, { vip: null })
          continue
        }
        await this.removeClient(saved[i].id)
      }
    }
    for (const [id] of tmpGuilds) {
      this.addClient(id)
    }
  }

  /**
   * Remove a guild from the guild_settings table.
   * @param  {String}             id The guild ID to remove.
   * @return {(Number|undefined)}    Returns 0 on success or undefined.
   */
  removeClient (id) {
    return this.del({ table: 'guild_settings', where: { id } })
  }

  /**
   * Update a guild entry in the guild_settings table.
   * @param  {String}             id   The ID of the guild.
   * @param  {Object}             data The data to update. Property names should match column names.
   * @return {(Number|undefined)}      Returns 0 on success or undefined.
   */
  updateClient (id, data) {
    return this.update({ table: 'guild_settings', data, where: { id } })
  }

  /**
   * Update the default status of the bot.
   * @param  {Object}             status      The status to make default.
   * @param  {String}             status.name The name of the status.
   * @param  {Number}             status.type The type of the status.
   * @return {(Number|undefined)}             Returns 0 on success or undefined.
   */
  updateDefaultStatus (status) {
    return this.update({ table: 'games', data: status, where: { default: 1 } })
  }

  /**
   * Update the default prefix in the guild_settings table. Not available with SQLite.
   * @param {String} prefix The prefix to set as default.
   */
  updateDefaultPrefix (prefix) {
    this._knex.schema.alterTable('guild_settings', (table) => {
      table.string('prefix').defaultTo(prefix)
    })
  }

  // private methods
  /**
   * Get the number of rows in a table.
   * @private
   * @param   {String}             table The name of the table.
   * @return  {(Number|undefined)}       Returns the number of rows on success or undefined.
   */
  count (table) {
    return this._knex(table).count('*')
    .then((val) => val[0]['count(*)'])
    .catch((e) => undefined)
  }

  /**
   * Delete an entry from a table.
   * @private
   * @param   {Object}             data       The query data.
   * @param   {String}             data.table The name of the table.
   * @param   {Object}             data.where The condition to be met to find what to delete. Property name should match column name.
   * @return  {(Number|undefined)}            Returns 0 on success or undefined.
   */
  del ({ table, where }) {
    return this._knex(table).where(where).del()
    .then((success) => 0)
    .catch((e) => undefined)
  }

  /**
   * Insert an entry into a table.
   * @private
   * @param   {Object}             data       The query data.
   * @param   {String}             data.table The name of the table.
   * @param   {Object}             data.data  The data to insert. Property names should match column names.
   * @return  {(Number|undefined)}            Returns 0 on success or undefined.
   */
  insert ({ table, data }) {
    return this._knex(table).insert(data)
    .then((success) => 0)
    .catch((e) => undefined)
  }

  /**
   * Select entries from a table.
   * @private
   * @param   {Object}               data               The query data.
   * @param   {String}               data.table         The name of the table.
   * @param   {(String[]|String)}    [data.columns='*'] The column(s) to select.
   * @param   {Number}               [data.offset=0]    The amount of rows to skip before selecting.
   * @param   {Number}               [data.limit=null]  The amount of rows to select. Will be set to all columns if null.
   * @param   {Object}               [data.where=true]  The condition to match your selection against. Property name should match column name.
   * @return  {(Object[]|undefined)}                    Returns array of rows on success or undefined.
   */
  async select ({ table, columns = '*', offset = 0, limit = null, where = true }) {
    if (!limit) limit = await this.count(table)
    return this._knex(table).select(columns).where(where).offset(offset).limit(limit)
    .then((rows) => rows)
    .catch((e) => undefined)
  }

  /**
   * Update entries in a table.
   * @private
   * @param   {Object}             data       The query data.
   * @param   {String}             data.table The name of the table.
   * @param   {Object}             data.where The condition to be met to find what to update. Property name should match column name.
   * @param   {Object}             data.data  The data to update. Property names should match column names.
   * @return  {(Number|undefined)}            Returns 0 on success or undefined.
   */
  update ({ table, where, data }) {
    return this._knex(table).where(where).update(data)
    .then((success) => 0)
    .catch((e) => undefined)
  }
}

module.exports = DatabaseManager

const moment = require('moment')
const colors = require('colors')

/**
 * Class representing a logger.
 */
class Logger {
  /**
   * Log something.
   * @param {String} str             The string to log (can also be something with a toString method).
   * @param {String} [color='white'] The color that the message should be in.
   */
  log (str, color = 'white') {
    if (typeof str !== 'string') str = str.toString()
    console.log(colors.gray(`${moment().format('MM/DD HH:mm:ss')}`) + ' | ' +
    colors[color](`BZZT ${str.toUpperCase()} BZZT`))
  }
  /**
   * Log something in green for success.
   * @param {String} str The string to log (can also be something with a toString method).
   */
  success (str) {
    this.log(str, 'green')
  }
  /**
   * Log something in yellow for warning.
   * @param {String} str The string to log (can also be something with a toString method).
   */
  warn (str) {
    this.log(str, 'yellow')
  }
  /**
   * Log something in red for error.
   * @param {String} err The string to log (can also be something with a toString method).
   */
  error (err) {
    this.log(err, 'red')
  }
}

module.exports = Logger

/**
 * Class representing a permission.
 */
class Permission {
  /**
   * Create a permission.
   * @param {String}   name                                The name of the permission.
   * @param {Number}   level                               The level of the permission (0 is the bottom).
   * @param {Function} [check=function () { return true }] A test to see if a member has this permission.
   */
  constructor ({ name, level, check }) {
    if (typeof name !== 'string') throw Error(`permission cannot have name ${name}`)
    if (isNaN(level)) throw Error(`permission cannot have level ${level}`)
    /**
     * The name of the permission.
     * @type {String}
     */
    this.name = name
    /**
     * The level of the permission (0 is the bottom).
     * @type {String}
     */
    this.level = level
    /**
     * A test to see if a member has this permission.
     * @type {Function}
     */
    this.check = check || function () { return true }
  }
  /**
   * Denial message telling the user what level permission they need.
   * @return {String} Denial message including permission name.
   */
  deny () {
    return `Must be ${this.name}!`
  }
}

module.exports = Permission

/**
 * Class representing a setting.
 */
class Setting {
  /**
   * Create a setting.
   * @param {Object}   data          An object with data to assign to the Setting.
   * @param {String}   data.name     The name of the Setting.
   * @param {String}   data.code     The (camelCase) code of the setting.
   * @param {Function} data.onChange A function that gets executed whenever the value of the setting is changed.
   * @param {Client}   bot           The bot object.
   */
  constructor ({ name, code, onChange }, bot) {
    if (typeof name !== 'string') throw new Error(`setting cannot have name ${name}`)
    if (typeof code !== 'string') throw new Error(`setting cannot have code ${code}`)
    if (typeof onChange !== 'function') throw new Error(`setting cannot have onChange function ${onChange}`)
    /**
     * The name of the setting.
     * @type {String}
     */
    this.name = name
    /**
     * The (camelCase) code of the setting.
     * @type {String}
     */
    this.code = code
    /**
     * A function that gets executed whenever the value of the setting is changed.
     * @type {Function}
     */
    this.onChange = onChange
    /**
     * The value of the setting.
     * @type {*}
     */
    this.value = bot.config.DEFAULT[this.code]
  }
  /**
   * Sets the value of the setting.
   * @param  {*}      value The value to set.
   * @param  {Client} bot   The bot object.
   * @return {String}       Confirmation message.
   */
  setValue (value, bot) {
    if (this.value === value) return `${this.name} is already ${this.value}!`
    this.value = bot.config.DEFAULT[this.code] = value
    this.save(bot.config)
    this.onChange(bot, value)
    return `${this.name} set to ${this.value}!`
  }
  /**
   * Save settings to file.
   * @param {Object} config The client's config object.
   */
  save (config) {
    require('fs').writeFile('./config.json', JSON.stringify(config, undefined, 2), (err) => {
      if (err) throw err
    })
  }
}

module.exports = Setting

const StatusType = require('./StatusType')
/**
 * Class representing the bot status.
 */
class Status {
  /**
   * Create a status.
   */
  constructor () {
    /**
     * The current status.
     * @type     {Object}
     * @property {String} name The name of the current status.
     * @property {Number} type The type of the current status.
     */
    this.current = { name: '', type: 1 }
    /**
     * StatusType data
     * @type {StatusType}
     */
    this.type = new StatusType()
    /**
     * The interval for automatic status changes
     * @type {Timeout}
     */
    this.interval = undefined
  }
  /**
   * Set the status to the default.
   * @param {Client} bot The bot object.
   */
  default (bot) {
    this.setStatus(bot, bot.config.DEFAULT.status)
  }
  /**
   * Stop changing status automatically
   */
  endRotate () {
    if (this.interval) this.interval = clearInterval(this.interval)
  }
  /**
   * Set the status of the bot
   * @param {Client} bot             The bot object.
   * @param {Object} [status]        Status to set to, if none is given it will be chosen at random
   * @param {String} [status.name]   Name of status
   * @param {Number} [status.type=0] Type of status. 0 is playing, 1 is streaming (Twitch only [Unsupported]), 2 is listening, 3 is watching.
   */
  async setStatus (bot, { name, type = 0 }) {
    let status
    if (!name) {
      const statuses = (await bot.dbm.getStatuses()).map((status) => {
        return { name: status.name, type: status.type }
      })
      status = statuses[Math.round(Math.random() * (statuses.length - 1))]
      if (statuses.length > 1) {
        while (status.name === this.current.name) {
          status = statuses[Math.round(Math.random() * (statuses.length - 1))]
        }
      }
    }
    status = { name, type }
    bot.logger.log(`${this.type[status.type]} ${status.name}`, 'cyan')
    bot.editStatus('online', status)
    this.current = status
  }
  /**
   * Set the status of the bot
   * @param {Client} bot The bot object.
   */
  startRotate (bot) {
    this.interval = setInterval(() => { this.setStatus(bot) }, 43200000)
  }
}

module.exports = Status

class StatusType {
  constructor () {
    /**
     * Enum for status types
     * @type     {Object}
     * @property {Number} PLAYING   0
     * @property {Number} PLAYING   1 (Twitch only)
     * @property {Number} LISTENING 2
     * @property {Number} WATCHING  3
     */
    this.num = {
      PLAYING: 0,
      STREAMING: 1,
      LISTENING: 2,
      WATCHING: 3
    }
  }
  /**
   * Get a status type string by num.
   * @param  {Number} num The number type to lookup.
   * @return {String}     The nice string name.
   */
  getStatusName (num) {
    const strings = {
      PLAYING: 'Playing',
      STREAMING: 'Streaming',
      LISTENING: 'Listening',
      WATCHING: 'Watching'
    }
    for (let key of this.num) {
      if (this.num[key] === num) return strings[key]
    }
  }
}

module.exports = StatusType

/**
 * Class representing a Toggle.
 * @extends {Setting}
 * @prop    {Boolean} value The value of the Toggle.
 */
class Toggle extends require('./Setting.js') {
  /**
   * Set the value of the Toggle to true.
   * @param  {Client} bot   The bot object.
   * @return {String}       Confirmation message.
   */
  enable (bot) {
    return this.setValue(true, bot)
  }
  /**
   * Set the value of the Toggle to false.
   * @param  {Client} bot   The bot object.
   * @return {String}       Confirmation message.
   */
  disable (bot) {
    return this.setValue(false, bot)
  }
}

module.exports = Toggle
