const DatabaseManager = require('./DatabaseManager.js')
const Logger = require('./Logger.js')
const Status = require('./Status.js')
/**
 * Class representing a DataClient.
 * @extends {Client}
 */
class DataClient extends require('eris') {
  /**
   * Create a client.
   * @param {Object}  config                         The bot config data.
   * @param {String}  config.TOKEN                   The bot token.
   * @param {Object}  config.DB_CREDENTIALS          The database credentials.
   * @param {String}  config.DB_CREDENTIALS.database The name of the database.
   * @param {String}  config.DB_CREDENTIALS.host     The host address of the server.
   * @param {String}  config.DB_CREDENTIALS.user     The username to login with.
   * @param {String}  config.DB_CREDENTIALS.password The password to login with.
   * @param {Object}  config.DEFAULT                 The bots default values.
   * @param {String}  config.DEFAULT.prefix          The default prefix.
   * @param {Boolean} config.DEFAULT.rotateStatus    The default for changing bot status.
   * @param {Object}  config.DEFAULT.status          The default bot status.
   * @param {String}  config.DEFAULT.status.name     The default bot status name.
   * @param {Number}  config.DEFAULT.status.type     The default bot status type.
   * @param {Boolean} config.DEFAULT.status.default  The boolean indicating to the database that this is the default status.
   */
  constructor (config) {
    super(config.TOKEN)
    /**
     * The bot config data.
     * @type     {Object}
     * @property {String}  config.TOKEN                   The bot token.
     * @property {Object}  config.DB_CREDENTIALS          The database credentials.
     * @property {String}  config.DB_CREDENTIALS.database The name of the database.
     * @property {String}  config.DB_CREDENTIALS.host     The host address of the server.
     * @property {String}  config.DB_CREDENTIALS.user     The username to login with.
     * @property {String}  config.DB_CREDENTIALS.password The password to login with.
     * @property {Object}  config.DEFAULT                 The bots default values.
     * @property {String}  config.DEFAULT.prefix          The default prefix.
     * @property {Boolean} config.DEFAULT.rotateStatus    The default for changing bot status.
     * @property {Object}  config.DEFAULT.status          The default bot status.
     * @property {String}  config.DEFAULT.status.name     The default bot status name.
     * @property {Number}  config.DEFAULT.status.type     The default bot status type.
     * @property {Boolean} config.DEFAULT.status.default  The boolean indicating to the database that this is the default status.
     */
    this.config = config
    /**
     * The logger.
     * @type {Logger}
     */
    this.logger = new Logger()
    /**
     * The DatabaseManager.
     * @type {DatabaseManager}
     */
    this.dbm = new DatabaseManager(config.DB_CREDENTIALS, Logger)
    /**
     * The Status handler.
     * @type {Status}
     */
    this.status = new Status()
    /**
     * The command map.
     * @type {Map}
     */
    this.commands = new Map()
    /**
     * The command alias map.
     * @type {Map}
     */
    this.aliases = new Map()
    /**
     * The permission map.
     * @type {Map}
     */
    this.permissions = new Map()
    /**
     * The setting map.
     * @type {Map}
     */
    this.settings = new Map()
    /**
     * The toggle map.
     * @type {Map}
     */
    this.toggles = new Map()
  }
}

module.exports = DataClient

const moment = require('moment')
const colors = require('colors/safe')

/**
 * Class representing a logger.
 */
class Logger {
  /**
   * Log something.
   * @param {String} content         The string to log (can also be something with a toString method).
   * @param {String} [color='white'] The color that the message should be in.
   */
  log (content, color = 'white') {
    const time = moment().format('MM/DD HH:mm:ss')
    console.log(colors.gray(time) + ' | ' + colors[color](content))
  }
  /**
   * Log something in green for success.
   * @param {String} content The string to log (can also be something with a toString method).
   */
  success (content) {
    const time = moment().format('MM/DD HH:mm:ss')
    console.log(colors.gray(time) + ' | ' + colors.green(content))
  }
  /**
   * Log something in yellow for warning.
   * @param {String} content The string to log (can also be something with a toString method).
   */
  warn (content) {
    const time = moment().format('MM/DD HH:mm:ss')
    console.log(colors.gray(time) + ' | ' + colors.yellow(content))
  }
  /**
   * Log something in red for error.
   * @param {String} error The string to log (can also be something with a toString method).
   */
  error (error) {
    if (error.stack) error = error.stack
    const time = moment().format('MM/DD HH:mm:ss')
    console.log(colors.gray(time) + ' | ' + colors.red(error))
  }
}

module.exports = Logger

/**
 * Class representing a permission.
 */
class Permission {
  /**
   * Create a permission.
   * @param {Object}   data                                     The permission data.
   * @param {String}   data.name                                The name of the permission.
   * @param {Number}   data.level                               The level of the permission (0 is the bottom).
   * @param {Function} [data.check=function () { return true }] A test to see if a member has this permission.
   */
  constructor (data) {
    const { name, level, check } = data
    if (typeof name !== 'string') throw Error(`permission cannot have name ${name}`)
    if (isNaN(level)) throw Error(`permission cannot have level ${level}`)
    /**
     * The name of the permission.
     * @type {String}
     */
    this.name = name
    /**
     * The level of the permission (0 is the bottom).
     * @type {String}
     */
    this.level = level
    /**
     * A test to see if a member has this permission.
     * @type {Function}
     */
    this.check = typeof check === 'function' ? check : function () { return true }
  }
  /**
   * Denial message telling the user what level permission they need.
   * @return {String} Denial message including permission name.
   */
  deny () {
    return `Must be ${this.name}!`
  }
}

module.exports = Permission

/**
 * Class representing a setting.
 */
class Setting {
  /**
   * Create a setting.
   * @param {DataClient} bot           The bot object.
   * @param {Object}     data          An object with data to assign to the Setting.
   * @param {String}     data.name     The name of the Setting.
   * @param {String}     data.code     The (camelCase) code of the setting.
   * @param {Function}   data.onChange A function that gets executed whenever the value of the setting is changed.
   */
  constructor (bot, data) {
    const { name, code, onChange } = data
    if (typeof name !== 'string') throw new Error(`setting cannot have name ${name}`)
    if (typeof code !== 'string') throw new Error(`setting cannot have code ${code}`)
    if (typeof onChange !== 'function') throw new Error(`setting cannot have onChange function ${onChange}`)
    /**
     * The name of the setting.
     * @type {String}
     */
    this.name = name
    /**
     * The (camelCase) code of the setting.
     * @type {String}
     */
    this.code = code
    /**
     * A function that gets executed whenever the value of the setting is changed.
     * @type {Function}
     */
    this.onChange = onChange
    /**
     * The value of the setting.
     * @type {*}
     */
    this.value = bot.config.DEFAULT[this.code]
  }
  /**
   * Sets the value of the setting.
   * @param  {*}      value The value to set.
   * @param  {DataClient} bot   The bot object.
   * @return {String}       Confirmation message.
   */
  setValue (value, bot) {
    if (this.value === value) return `${this.name} is already ${this.value}!`
    this.value = bot.config.DEFAULT[this.code] = value
    this.save(bot.config)
    this.onChange(bot, value)
    return `${this.name} set to ${this.value}!`
  }
  /**
   * Save settings to file.
   * @param {Object} config The client's config object.
   */
  save (config) {
    require('fs').writeFile('./config.json', JSON.stringify(config, undefined, 2), (err) => {
      if (err) throw err
    })
  }
}

module.exports = Setting

/**
 * Class representing the bot status.
 */
const StatusType = require('./StatusType.js')
class Status {
  /**
   * Create a status.
   */
  constructor () {
    /**
     * The current status.
     * @type     {Object}
     * @property {String} name The name of the current status.
     * @property {Number} type The type of the current status.
     */
    this.current = { name: '', type: 0 }
    /**
     * StatusType data
     * @private
     * @type {StatusType}
     */
    this._type = new StatusType()
    /**
     * The interval for automatic status changes
     * @private
     */
    this._interval = undefined
  }
  /**
   * Set the status to the default.
   * @param {DataClient} bot The bot object.
   */
  default (bot) {
    const { name, type } = bot.config.DEFAULT.status
    this.setStatus(bot, { name, type })
  }
  /**
   * Stop changing status automatically
   */
  endRotate () {
    if (this._interval) this._interval = clearInterval(this._interval)
  }
  /**
   * Set the status of the bot
   * @param {DataClient} bot             The bot object.
   * @param {Logger} bot.logger      The logger.
   * @param {Object} [status]        Status to set to, if none is given it will be chosen at random
   * @param {String} [status.name]   Name of status
   * @param {Number} [status.type=0] Type of status. 0 is playing, 1 is streaming (Twitch only [Unsupported]), 2 is listening, 3 is watching.
   */
  async setStatus (bot, status) {
    if (!status || !status.name) {
      status = this.current
      const statuses = await bot.dbm.getStatuses()
      if (statuses.length > 1) {
        while (status.name === this.current.name) {
          status = statuses[Math.round(Math.random() * (statuses.length - 1))]
        }
      } else {
        status = statuses[0]
      }
    }
    bot.logger.log(`${this._type.getStatusName(status.type)} ${status.name}`, 'cyan')
    bot.editStatus('online', status)
    this.current = status
  }
  /**
   * Set the status of the bot
   * @param {DataClient} bot The bot object.
   */
  startRotate (bot) {
    this._interval = setInterval(() => { this.setStatus(bot) }, 43200000)
  }
}

module.exports = Status

class StatusType {
  constructor () {
    /**
     * Enum for status types
     * @type     {Object}
     * @property {Number} PLAYING   0
     * @property {Number} STREAMING 1 (Twitch only)
     * @property {Number} LISTENING 2
     * @property {Number} WATCHING  3
     */
    this.num = {
      PLAYING: 0,
      STREAMING: 1,
      LISTENING: 2,
      WATCHING: 3
    }
  }
  /**
   * Get a status type string by num.
   * @param  {Number} num The number type to lookup.
   * @return {String}     The nice string name.
   */
  getStatusName (num) {
    const strings = {
      PLAYING: 'Playing',
      STREAMING: 'Streaming',
      LISTENING: 'Listening',
      WATCHING: 'Watching'
    }
    for (let key in this.num) {
      if (this.num[key] === num) return strings[key]
    }
  }
}

module.exports = StatusType

/**
 * Class representing a Toggle.
 * @extends {Setting}
 * @prop    {Boolean} value The value of the Toggle.
 */
class Toggle extends require('./Setting.js') {
  /**
   * Set the value of the Toggle to true.
   * @param  {DataClient} bot The bot object.
   * @return {String}         Confirmation message.
   */
  enable (bot) {
    return this.setValue(true, bot)
  }
  /**
   * Set the value of the Toggle to false.
   * @param  {DataClient} bot The bot object.
   * @return {String}         Confirmation message.
   */
  disable (bot) {
    return this.setValue(false, bot)
  }
}

module.exports = Toggle

